import ReactDOM from "react-dom/client";
import { BrowserRouter, Routes, Route } from "react-router-dom";
import NavigationLinks from "./pages/NavigationLinks";
import Home from "./pages/Home";
// import Blogs from "./pages/Student";
// import Contact from "./pages/Contact";
import NoPage from "./pages/NoPage";
import Student from "./pages/Student";
import StudentDetails from "./pages/StudentDetails";
import "./App.css";
import { useState } from "react";

export default function App() {
  const [detailsList, setDetailsList] = useState([]);
  const add_details = (details) => {
    console.log(details);
    setDetailsList([...detailsList, details]);
    // setHandelDiv(true);
  };
  return (
    <BrowserRouter>
      <Routes>

        <Route path="/" element={<NavigationLinks />}>
          <Route index element={<Home />} />
          <Route path="student" element={<Student add_details={add_details}  />} />
          <Route path="studentDetails" element={<StudentDetails detailsList={detailsList} />} />

          <Route path="editDetails"  
          // element={<Student  add_details={add_details}/>}
        //    render={(props) => (

        // )}
        >
                      <Route path="/:id" component={<Student add_details={add_details}/>}/>

          {/* <Student  id={props.match.params.id} add_details={add_details}/> */}
        </Route>
          <Route path="nikita" element={<StudentDetails detailsList={detailsList} />} />
          {/* <Route path="contact" element={<Contact />} /> */}
          <Route path="*" element={<NoPage />} />
        </Route>

      </Routes>
    </BrowserRouter>
  );
}

const root = ReactDOM.createRoot(document.getElementById("root"));
root.render(<App />);

