import React, { useRef, useState } from "react";
import { useLocation, useParams } from "react-router-dom";

const Student = (props) => {
  const { id } = useParams();   
  console.log(id);
  // const location = useLocation(); // returns the location object 

  //returns the :id
console.log(location);
  const [details, setDetails] = useState({
    name: "",
    department: "",
    rating: "",
  });

  //if(id!=undefined){
  //  setDetails = 
  // }
  const updateInput = (event, ch) => {
    details[ch] = event.target.value;
    setDetails(details);
  };
  const refForm = useRef(null);
  return (
    <div>
      <form ref={refForm}>
        <div>
          <h1>EMPLOYEE FORM HANDLING</h1>
          <label>
            Name :
            <input
              className="name"
              value={details.name}
              onChange={(event) => updateInput(event, "name")}
            />
          </label>
          <br />
          <label>
            Department :
            <input
              className="department"
              //     value={details.department}
              onChange={(event) => updateInput(event, "department")}
            />
          </label>
          <br />
          <label>
            Rating :
            <input
              className="rating"
              // value={details.rating}
              onChange={(event) => updateInput(event, "rating")}
            />
          </label>
          <br />
          <button
            onClick={() => {
              props.add_details(details);
              setDetails({
                name: "",
                department: "",
                rating: "",
              });
              refForm.current.reset();
            }}
            type="button"
          >
            Submit
          </button>
        </div>
      </form>
    </div>
  );
};
export default Student;
